async def safe_answer(q):
    try:
        await q.answer()
    except BadRequest as e:
        s = str(e).lower()
        if "query is too old" in s or "query id is invalid" in s:
            return
        raise



def _sanitize_text(text: str) -> str:
    if text is None:
        return ""

    out = str(text)
    # Fix common mojibake fragments from legacy rows/texts.
    repl = {
        "dost??pu": "dost?pu",
        "faktur??": "faktur?",
        "miesi??ca": "miesi?ca",
        "miesi??c": "miesi?c",
        "Miesi??ce": "Miesi?ce",
        "Podgl??d": "Podgl?d",
        "Wys??ane": "Wys?ane",
        "Wys??ana": "Wys?ana",
        "Wys??any": "Wys?any",
        "Wy??lij": "Wy?lij",
        "zdj??cie": "zdj?cie",
        "ksi??gowej": "ksi?gowej",
        "Wybierz miesic": "Wybierz miesi?c",
        "Brak dostepu": "Brak dost?pu",
        "Brak dost??pu": "Brak dost?pu",
    }
    for bad, good in repl.items():
        out = out.replace(bad, good)
    return out


async def safe_edit(q, text, kb=None):
    try:
        return await q.edit_message_text(_sanitize_text(text), reply_markup=kb)
    except BadRequest as e:
        s = str(e).lower()
        if "message is not modified" in s:
            return
        if "message can't be edited" in s or "message to edit not found" in s:
            try:
                await q.message.reply_text(_sanitize_text(text), reply_markup=kb)
            except:
                pass
            return
        raise


