def nice_month_report(update: Update, m: str) -> str:
    st = compute_month_stats(update, m)
    todo_txt = "brak " if not st["todo"] else ", ".join(map(str, st["todo"][:20])) + (" " if len(st["todo"]) > 20 else "")
    miss_txt = "brak " if not st["todo_missing_price"] else ", ".join(map(str, st["todo_missing_price"][:20])) + (" " if len(st["todo_missing_price"]) > 20 else "")

    return (
        f" PODGLD  {m}\n"
        f"\n"
        f" Brutto razem: {st['gross']:.2f} z\n"
        f" Netto razem: {st['net']:.2f} z\n"
        f" VAT razem: {st['vat']:.2f} z\n\n"
        f" VAT (brutto): {st['vat_gross']:.2f} z\n"
        f" Bez VAT (brutto): {st['novat_gross']:.2f} z\n\n"
        f" Gotowe: {st['ok']}    Wysane: {st['sent']}\n"
        f" Do sprawdzenia: {len(st['todo'])}  |   Brak kwoty: {len(st['todo_missing_price'])}\n\n"
        f" Wiersze TODO: {todo_txt}\n"
        f" Brak kwoty: {miss_txt}\n"
        f""
    )


# =========================
#  ZIP DO KSIGOWEJ
# =========================
def build_month_zip(update: Update, m: str) -> tuple[bytes, str]:
    rows = get_all_rows(update)
    out_rows = []
    links = []

    for r in rows:
        r = pad_row(r, COL_FILE)
        if not (r[COL_DATE - 1] or "").startswith(m):
            continue
        out_rows.append(r[:COL_FILE])  # do K

        if r[COL_FILE - 1]:
            links.append(r[COL_FILE - 1])

    # CSV (z nagwkiem)
    csv_buf = io.StringIO()
    w = csv.writer(csv_buf)
    w.writerow(["data", "numer", "firma", "brutto", "typ", "vat", "netto", "kategoria", "user", "status", "plik"])
    for r in out_rows:
        w.writerow(r)
    csv_bytes = csv_buf.getvalue().encode("utf-8")

    # linki
    links_bytes = ("\n".join(links) + "\n").encode("utf-8")

    # ZIP
    zip_buf = io.BytesIO()
    with zipfile.ZipFile(zip_buf, "w", zipfile.ZIP_DEFLATED) as z:
        z.writestr(f"arkusz_{m}.csv", csv_bytes)
        z.writestr(f"linki_{m}.txt", links_bytes)

    return zip_buf.getvalue(), f"danex_ksiegowa_{m}.zip"


# =========================
#  AUTO-NEXT / CONTINUE
# =========================
async def open_row(update: Update, q, uid: int, row_no: int, note: str = ""):
    r = get_row(update, row_no)
    r = pad_row(r, COL_FILE)
    link = (r[COL_FILE - 1] or "").strip()
    m = month_from_row(r)

    # zapamitaj miesic do kontynuacji
    if uid not in STATE:
        STATE[uid] = {}
    STATE[uid]["last_month"] = m
    STATE[uid]["last_row"] = str(row_no)

    gross = parse_amount(r[COL_GROSS - 1])
    inv_type = (r[COL_TYPE - 1] or "").strip() or "(brak typu)"

    msg = (
        f"{note}\n"
        f" Wiersz #{row_no}  ({m})\n"
        f"Firma: {r[COL_COMP-1] or '-'}\n"
        f"Nr: {r[COL_NO-1] or '-'}\n"
        f"Typ: {inv_type}\n"
        f"Kwota brutto: {gross:.2f} z\n\n"
        f"Kliknij  Ustaw kwot, jeli brak / le.\n"
        f"Kliknij  OK, jak gotowe.\n"
    ).strip()

    return await safe_edit(q, msg, kb_invoice(row_no, link, m))


# =========================
#  MAMA-FRIENDLY NAPRAW
# =========================
def mama_fix_message(row_no: int, r: list) -> str:
    r = pad_row(r, COL_FILE)
    m = month_from_row(r)
    gross = parse_amount(r[COL_GROSS - 1])
    inv_type = (r[COL_TYPE - 1] or "").strip()

    tips = []
    if gross <= 0:
        tips.append("1) Kliknij ** Ustaw kwot** i wpisz brutto z faktury (np. 123,45).")
    if not (inv_type or "").strip():
        tips.append("2) Jeli brak typu  ustaw w arkuszu typ (VAT / Bez VAT).")
    tips.append("3) Na koniec kliknij ** OK (gotowe)**.")
    return (
        f" NAPRAW (Mama-friendly)\n"
        f" Wiersz #{row_no} ({m})\n"
        f"Firma: {r[COL_COMP-1] or '-'} | Nr: {r[COL_NO-1] or '-'}\n"
        f"Kwota brutto: {gross:.2f} z | Typ: {inv_type or '(brak)'}\n\n"
        + "\n".join(tips)
    )


# =========================
#  CALLBACK MAIN
# =========================
async def on_click(update: Update, ctx: ContextTypes.DEFAULT_TYPE):
    q = update.callback_query
    if not q:
        return

    if not is_allowed(update):
        try:
            await q.answer("Brak dostepu", show_alert=True)
        except:
            pass
        return

    await safe_answer(q)

    uid = update.effective_user.id
    data = (q.data or "").strip()
    mutating_prefixes = (
        "add:type:",
        "i:price:",
        "i:vat:",
        "i:ok:",
        "i:sent:",
        "i:fix:",
        "i:ocr:",
        "i:ocrf:",
    )
    if any(data.startswith(pf) for pf in mutating_prefixes) and not is_operator(update):
        return await safe_edit(q, "Tylko operator lub admin moze zmieniac dane.", kb_page(1))


    # --- MENU PAGES ---
    if data.startswith("m:page:"):
        p = int(data.split(":")[-1])
        return await safe_edit(q, "Menu:", kb_page(p))

    # --- DODAJ FAKTUR: WYBR VAT/BEZ VAT ---
    if data == "m:add":
        STATE[uid] = {"mode": "add_wait_type"}
        return await safe_edit(q, "Wybierz typ faktury:", kb_add_type())

    if data.startswith("add:type:"):
        # po wyborze typu -> czekamy na plik (obsuy to handler plikw)
        t = data.split(":")[-1]
        inv_type = TYPE_VAT if t == "VAT" else TYPE_NO_VAT
        STATE[uid] = {"mode": "add_wait_file", "inv_type": inv_type}
        return await safe_edit(q, f"Wyslij teraz fakture (PDF/zdjecie).\nWybrany typ: {inv_type}", kb_page(1))

    # --- YEAR PICKERS ---
    if data == "m:todo_year":
        return await safe_edit(q, "Wybierz rok (Do sprawdzenia):", kb_years("todo"))
    if data == "m:todo_missing_year":
        return await safe_edit(q, "Wybierz rok (Brak kwoty):", kb_years("todo_missing"))
    if data == "m:snap_year":
        return await safe_edit(q, "Wybierz rok (Podglad):", kb_years("snap"))
    if data == "m:pack_year":
        return await safe_edit(q, "Wybierz rok (ZIP do ksiegowej):", kb_years("pack"))

    # --- YEAR selection ---
    if ":Y:" in data:
        prefix, _, rest = data.partition(":Y:")
        if rest == "custom":
            STATE[uid] = {"mode": f"{prefix}_year_custom"}
            return await safe_edit(q, "Podaj rok YYYY (np. 2026).", kb_page(1))
        if rest.isdigit():
            year = int(rest)
            return await safe_edit(q, f"Wybierz miesiac ({year}):", kb_months_of_year(prefix, year))

    # --- MONTH selection ---
    if ":M:" in data:
        prefix, _, m = data.partition(":M:")
        m = ensure_month(m) or today_ym()

        if prefix == "snap":
            return await safe_edit(q, nice_month_report(update, m), kb_page(2))

        if prefix in ("todo", "todo_missing"):
            st = compute_month_stats(update, m)
            lst = st["todo"] if prefix == "todo" else st["todo_missing_price"]
            if not lst:
                return await safe_edit(q, f"Nic do poprawy w {m}.", kb_page(2))
            # otwrz pierwszy
            first = lst[0]
            return await open_row(update, q, uid, first, note=f" Lista ({m})  start")

        if prefix == "pack":
            zip_bytes, filename = build_month_zip(update, m)
            await q.message.reply_document(document=zip_bytes, filename=filename)
            return await safe_edit(q, f"ZIP wyslany dla {m}.", kb_page(2))

    # --- CONTINUE ---
    if data == "m:continue":
        m = (STATE.get(uid, {}).get("last_month") or "").strip()
        last_row = STATE.get(uid, {}).get("last_row", "")
        after = int(last_row) if last_row.isdigit() else None
        if not m:
            return await safe_edit(q, "Brak kontekstu. Wejdz w Do sprawdzenia albo Podglad miesiaca.", kb_page(1))
        nxt = find_next_missing_price_in_month(update, m, after_row=after)
        if not nxt:
            return await safe_edit(q, f"Nic wiecej do poprawy w {m}.", kb_page(1))
        return await open_row(update, q, uid, nxt, note=" Kontynuuj")

    # --- OCR field picker ---
    if data.startswith("i:ocrf:"):
        parts = data.split(":")
        if len(parts) != 4 or not parts[2].isdigit():
            return
        row_no = int(parts[2])
        field = parts[3]
        field_names = {
            "no": "numer",
            "comp": "firme",
            "date": "date",
            "gross": "kwote brutto",
        }
        if field not in field_names:
            return
        STATE[uid] = {"mode": "edit_field", "row": str(row_no), "field": field}
        return await safe_edit(q, f"Wpisz nowa wartosc: {field_names[field]} (wiersz {row_no}).\nAnuluj: stop", kb_page(1))
    # --- INVOICE actions ---
    if data.startswith("i:"):
        _, action, row_s = data.split(":", 2)
        if not row_s.isdigit():
            return
        row_no = int(row_s)
        r = get_row(update, row_no)
        r = pad_row(r, COL_FILE)
        link = (r[COL_FILE - 1] or "").strip()
        m = month_from_row(r)

        # zapamitaj kontekst
        if uid not in STATE:
            STATE[uid] = {}
        STATE[uid]["last_month"] = m
        STATE[uid]["last_row"] = str(row_no)

        if action == "vat":
            # tryb tekstowy ustawiania VAT (obsuy handlers/messages.py)
            STATE[uid] = {"mode": "set_vat", "row": str(row_no)}
            return await safe_edit(
                q,
                " Ustaw VAT rcznie:\n"
                "Wpisz: 23 / 8 / 5 / ZW / NP\n"
                "Np. 23 albo ZW\n\n"
                " Anuluj: wpisz `stop`",
                kb_invoice(row_no, link, m),
            )


        if action == "open":
            return await safe_edit(q, f" Plik (wiersz {row_no}):\n{link or '(brak linku)'}", kb_invoice(row_no, link, m))

        if action == "price":
            # ustawiamy tryb tekstowy (obsuy handlers/messages.py)
            STATE[uid] = {"mode": "set_price", "row": str(row_no), "month": m}
            return await safe_edit(q, f"Podaj kwote brutto dla wiersza {row_no} (np. 123,45).", kb_invoice(row_no, link, m))

        if action == "ocr":
            return await safe_edit(q, f"Wybierz pole do poprawy OCR (wiersz {row_no}):", kb_ocr_fields(row_no, link, m))

        if action == "fix":
            msg = mama_fix_message(row_no, r)
            return await safe_edit(q, msg, kb_invoice(row_no, link, m))

        if action == "ok":
            miss = missing_fields(r)
            if miss:
                old_status = (r[COL_STATUS - 1] if len(r) >= COL_STATUS else "") or ""
                update_cell(update, row_no, COL_STATUS, STATUS_TODO)
                log_event("status_change", user_id=uid, row_no=row_no, old_status=old_status, new_status=STATUS_TODO, source="callbacks:i:ok:missing")
                return await safe_edit(q, f" Brakuje: {', '.join(miss)}\nKliknij  Napraw.", kb_invoice(row_no, link, m))

            old_status = (r[COL_STATUS - 1] if len(r) >= COL_STATUS else "") or ""
            update_cell(update, row_no, COL_STATUS, STATUS_OK)
            log_event("status_change", user_id=uid, row_no=row_no, old_status=old_status, new_status=STATUS_OK, source="callbacks:i:ok")
            await safe_edit(q, f"OK. Wiersz {row_no} -> {STATUS_OK}", kb_invoice(row_no, link, m))

            # AUTO-NEXT
            nxt = find_next_missing_price_in_month(update, m, after_row=row_no)
            if not nxt:
                return await q.message.reply_text(f" Koniec na dzi. W {m} nie ma ju rzeczy do poprawy.")
            try:
                return await open_row(update, q, uid, nxt, note=" Auto-next: nastpny")
            except:
                return

        if action == "sent":
            old_status = (r[COL_STATUS - 1] if len(r) >= COL_STATUS else "") or ""
            update_cell(update, row_no, COL_STATUS, STATUS_SENT)
            log_event("status_change", user_id=uid, row_no=row_no, old_status=old_status, new_status=STATUS_SENT, source="callbacks:i:sent")
            await safe_edit(q, f"Wiersz {row_no} -> {STATUS_SENT}", kb_invoice(row_no, link, m))

            # AUTO-NEXT
            nxt = find_next_missing_price_in_month(update, m, after_row=row_no)
            if not nxt:
                return await q.message.reply_text(f" W {m} nie ma ju kolejnych do poprawy.")
            try:
                return await open_row(update, q, uid, nxt, note=" Auto-next: nastpny")
            except:
                return

        if action == "next":
            nxt = find_next_missing_price_in_month(update, m, after_row=row_no)
            if not nxt:
                return await safe_edit(q, f"Nic wiecej do poprawy w {m}.", kb_invoice(row_no, link, m))
            return await open_row(update, q, uid, nxt, note=" Next")

    # fallback
    return await safe_edit(q, "Menu:", kb_page(1))


def register(app):
    app.add_handler(CallbackQueryHandler(on_click))






